import React, { useState } from 'react';
import bubble from '../sorting-algorithms/bubble';
import cocktail from '../sorting-algorithms/cocktail';
import gnome from '../sorting-algorithms/gnome';
import insertation from '../sorting-algorithms/insertation';
import pancake from '../sorting-algorithms/pancake';
import quick from '../sorting-algorithms/quick';
import selection from '../sorting-algorithms/selection';
import shell from '../sorting-algorithms/shell';
import { init } from '../utilities';

export let isSortingPaused = false;
export let isArrayReset = false;

const Menu = (props) => {
    const sorts = [
        bubble,
        insertation,
        selection,
        gnome,
        pancake,
        cocktail,
        shell,
        quick,
    ];

    const [disableButtons, setDisableButtons] = useState(false);
    const [stopButtonText, setStopButtonText] = useState('stop');
    const pause = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pause-fill" viewBox="0 0 16 16">
        <path d="M5.5 3.5A1.5 1.5 0 0 1 7 5v6a1.5 1.5 0 0 1-3 0V5a1.5 1.5 0 0 1 1.5-1.5zm5 0A1.5 1.5 0 0 1 12 5v6a1.5 1.5 0 0 1-3 0V5a1.5 1.5 0 0 1 1.5-1.5z" />
    </svg>;
    const play = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-play-fill" viewBox="0 0 16 16">
    <path d="m11.596 8.697-6.363 3.692c-.54.313-1.233-.066-1.233-.697V4.308c0-.63.692-1.01 1.233-.696l6.363 3.692a.802.802 0 0 1 0 1.393z"/>
  </svg>
    const handleClick = async (e, func) => {
        if (disableButtons) {
            return;
        }
        setDisableButtons(true);
        await func(props.array, props.colors, props.size, props.ms);
        isArrayReset = false;
        setDisableButtons(false);
    }

    return (
        <div className="menu">
            <div className="controls">
                <div className="sliders">
                    <label>values: </label>
                    <input
                        disabled={disableButtons}
                        type='range'
                        min={0}
                        max={17}
                        value={props.stepIndex}
                        onChange={props.handleChange}
                    />
                    <br />
                    <label>ms: </label>
                    <input
                        disabled={disableButtons}
                        type='range'
                        step={1}
                        min={0}
                        max={1000}
                        value={props.ms}
                        onChange={props.handleDelayChange}
                    />
                </div>
                <button onClick={() => {
                    isSortingPaused = !isSortingPaused
                    isSortingPaused === false ? setStopButtonText('stop') : setStopButtonText('start');
                }}></button>
                <button onClick={(e) => {
                    init(
                        props.array,
                        props.colors,
                        props.size,
                        props.height
                    )
                    setStopButtonText('stop');
                    isArrayReset = true;
                    isSortingPaused = false;
                }}>reset</button>
            </div>
            <div>
                <div className="sorts">
                    {sorts.map((sort, index) => {
                        return <button key={index} disabled={disableButtons} onClick={
                            (e) => handleClick(e, sort)
                        }>{sort.name}</button>;
                    })
                    }
                </div>
                <div className="more">
                    <a href="/">
                        <img
                            src={'http://pngimg.com/uploads/github/github_PNG40.png'}
                            height="50px" /></a>
                    <hr style={{ color: 'rgb(214, 214, 214)', width: '25%' }} />
                </div>
            </div>
        </div >
    )
}


export default Menu;
