import { stop, swap } from "../utilities";
import { isArrayReset } from "../components/buttonSet";

export default async function selection(values, size, ms, paused) {
    let minIndex;
    for (let i = 0; i < size - 1; i++) {
        minIndex = i;
        for (let j = i + 1; j < size; j++) {
            if (values[j] < values[minIndex])
                minIndex = j;
        }
        if (isArrayReset)
            return;
        await stop();
        await swap(values, i, minIndex, ms);
    }
    return values;
}
