import './App.css';
import React, { Component } from 'react';
import Canvas from './components/sketch';
import Menu from './components/controlMenu';
// react anti pattern
class App extends Component {
  constructor(props) {
    super(props);
    this.canvas = {
      width: 1280,
      height: 480
    }
    this.steps = [1, 2, 4, 5, 8, 10, 16, 20, 32, 40, 64, 80, 128, 160, 256, 320, 640, 1280];
    this.state = {
      stepIndex: 0,
      shapeWidth: 0,
      arraySize: 0,
      msDelay: 0,
    }
    this.array = [];
    this.colors = [];
  }

  handleChange = (e) => {
    const inputValue = parseInt(e.target.value);
    new Promise(resolve => {
      /*this.setState({
        stepIndex: inputValue,
        shapeWidth: this.steps[inputValue],
        arraySize: this.canvas.width / this.steps[inputValue]
      }, resolve)*/
      this.setState(
        (previousState,props) => {return{
            stepIndex: inputValue,
            shapeWidth: this.steps[inputValue],
            arraySize: this.canvas.width / this.steps[inputValue]
          }
        }
      );
      this.array.length = this.canvas.width / this.steps[inputValue];
      this.colors = [...this.array].fill(0)
    })
  }

  /*
    this.setState((prevState, props) => ({
      stepIndex: props.inputValue,
      shapeWidth: this.steps[props.inputValue],
      arraySize: this.canvas.width / this.steps[props.inputValue]
    }));

  */
  handleDelayChange = (e) => {
    this.setState({
      msDelay: parseInt(e.target.value)
    });
  }

  render() {
    return (
      <>
        <div className="App">
          <Canvas
            canvas={this.canvas}
            array={this.array}
            colors={this.colors}
            size={this.canvas.width}
            shapeWidth={this.state.shapeWidth}
            took={this.took}
          />
          <Menu
            steps={this.steps}
            stepIndex={this.state.stepIndex}
            array={this.array}
            colors={this.colors}
            shapeWidth={this.state.shapeWidth}
            size={this.state.arraySize}
            height={this.canvas.height}
            ms={this.state.msDelay}
            handleChange={this.handleChange}
            handleDelayChange={this.handleDelayChange}
          />
        </div>
      </>
    );
  }
}

export default App;
