import React, { Component } from 'react';
import './App.css';
import Canvas from './components/sketch';

class App extends Component {
  constructor(props) {
    super(props);
    this.canvas = {
      width: 1280,
      height: 480
    }
    this.state = {
      arraySize: 0,
      shapeWidth: 0,
      msDelay: 0
    }
    this.array = [];
    this.colorState = [];
  }

  handleChange = (e) => {
    let inputValue = parseInt(e.target.value);

    new Promise(resolve => {
      this.setState({
        shapeWidth: inputValue,
        arraySize: Math.floor(this.canvas.width / inputValue)
      });
      this.array.length = Math.floor(this.canvas.width / inputValue);
    })
  }

  handleDelayChange = (e) => {
    this.setState({
      msDelay: parseInt(e.target.value)
    });
  }
  render() {
    return <Canvas
      array={this.array}
      size={this.arraySize}
      canvas={this.canvas}
      shapeWidth={this.shapeWidth}
      />
  }
}

export default App;
