import { sleep, stop } from "../utilities";
import { isArrayReset } from "../components/buttonSet";


export default async function insertation(values, size, ms,paused) {
    for (let i = 1; i < size; i++) {
        let current = values[i];
        let j = i - 1;
        if (isArrayReset)
            return;
        await stop();
        while ((j > -1) && (current < values[j])) {
            values[j + 1] = values[j];
            j--;
        }
        await sleep(ms);
        values[j + 1] = current;
    }
    return values;
}
