import React from 'react';
import * as p5 from 'p5';

class Canvas extends React.Component {
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }
    Sketch = (p) => {
        let color1;
        let color2;
        p.setup = () => {
            p.createCanvas(this.props.canvas.width, this.props.canvas.height);
            color1 = p.color('#A16BFE');
            color2 = p.color('#DEB0DF');

        }
        p.draw = () => {
            p.background(255);
            for (let i = 0; i < this.props.size; i++) {
                p.noStroke();
                p.fill(0);
                p.rect(i * this.props.shapeWidth,
                    this.props.canvas.height - this.props.array[i],
                    this.props.shapeWidth, this.props.array[i]
                );
            }
        }
    }
    componentDidMount() {
        this.myP5 = new p5(this.Sketch, this.myRef.current);
        init(this.props.array,
            this.props.size,
            this.props.canvas.height);
    }
    componentWillReceiveProps(props) {
        if (this.props.size !== props.size) {
            this.myP5.remove();
            this.myP5 = new p5(this.Sketch, this.myRef.current);
            init(
                this.props.array,
                props.size,
                this.props.canvas.height
            );
        }
    }
    render() {
        return <div ref={this.myRef} />
    }
}

function init(array,size,height) {
    for (let i = 0; i < size; i++) {
        array[i] = Math.floor(Math.random() * (height - 100 + 1)) + 100;
    }
}
export default Canvas;
