import './App.css';
import React, { Component } from 'react';
import Canvas from './components/sketch';

class App extends Component {
  constructor(props) {
    super(props);
    this.canvas = {
      width: 1280,
      height: 480
    }
    this.steps = [1, 2, 4, 5, 8, 10, 16, 20, 32, 40, 64, 80, 128, 160, 256, 320, 640,1280];
    this.state = {
      stepIndex: 0,
      shapeWidth: 0,
      arraySize: 0,
      msDelay: 0
    }
    this.array = [];
  }

  handleChange = (e) => {
    const inputValue = parseInt(e.target.value);

    new Promise(resolve => {
      this.setState({
       stepIndex: inputValue,
       shapeWidth: this.steps[inputValue],
       arraySize: this.canvas.width / this.steps[inputValue]
      },resolve)
      this.array.length =  this.canvas.width / this.steps[inputValue];
    })
  }
  render() {
    return (
      <div className="App">
        <Canvas
          canvas={this.canvas}
          array={this.array}
          size={this.canvas.width}
          shapeWidth={this.state.shapeWidth}
        />
        <input
          type='range'
          min={0}
          max={this.steps.length - 1}
          value={this.state.stepIndex}
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default App;
