import './App.css';
import React, { Component } from 'react';
import Canvas from './components/sketch';
import ButtonSet from './components/buttonSet';

class App extends Component {
  constructor(props) {
    super(props);
    this.canvas = {
      width: window.innerWidth * 0.75,
      height: 480
    }
    this.state = {
      size: 0,
      shapeWidth: 0,
      msDelay: 0
    }
    this.array = [];
    this.colorStates = [];
  }

  handleArrayLenghtChange = (e) => {
    let inputValue = parseInt(e.target.value);
    /*new Promise(resolve => {

      let temp;
      const A = this.canvas.width / inputValue;
      if(A % 2 < 1)
        temp = Math.floor(A);
      else if (A % 2 >= 1)
        temp = Math.ceil(A);

      this.setState({
        size: inputValue,
        shapeWidth: inputValue > 0 ? temp : 0
      },resolve);
      this.array.length = inputValue;
      this.colorStates = [...this.array].fill(0);
      console.log(this.state.shapeWidth%2);
    })*/
    let temp = 640 - inputValue;
    new Promise(resolve => {
      this.setState({
        shapeWidth: temp,
        size: Math.floor(this.canvas.width / temp)
      }, resolve);
      this.array.length = inputValue;
    })
  }

  handleDelayChange = (e) => {
    this.setState({
      msDelay: parseInt(e.target.value)
    });
  }
  render() {
    return (
      <div className="App">
        <Canvas
          canvas={this.canvas}
          array={this.array}
          size={this.state.size}
          shapeWidth={this.state.shapeWidth}
          colorStates={this.colorStates}
        />


        <ButtonSet
          array={this.array}
          colorStates={this.colorStates}
          shapeWidth={this.shapeWidth}
          size={this.state.size}
          height={this.canvas.height}
          ms={this.state.msDelay}
          handleArrayLenghtChange={this.handleArrayLenghtChange}
          handleDelayChange={this.handleDelayChange}
        />
      </div>
    );
  }
}

export default App;
