import { stop, swap } from "../utilities";
import { isArrayReset } from "../components/buttonSet";

export default async function gnome(values, size, ms) {
    let n = size;
    let i = 0;
    while (i < n) {
        if (i === 0)
            i++;
        if (values[i] >= values[i - 1]) {
            i++;
        }
        else {
            await swap(values, i, i - 1, ms);
            i--;
        }
        if (isArrayReset)
            return;
        await stop();
    }
    return values;
}
