import React, { useState } from 'react';
import bubble from '../sorting-algorithms/bubble';
import cocktail from '../sorting-algorithms/cocktail';
import gnome from '../sorting-algorithms/gnome';
import insertation from '../sorting-algorithms/insertation';
import pancake from '../sorting-algorithms/pancake';
import quick from '../sorting-algorithms/quick';
import selection from '../sorting-algorithms/selection';
import shell from '../sorting-algorithms/shell';
import { init } from '../utilities';

export let isSortingPaused = false;
export let isArrayReset = false;

const Menu = (props) => {
    const sorts = [
        bubble,
        insertation,
        selection,
        gnome,
        pancake,
        cocktail,
        shell,
        quick,
    ];

    const [disableButtons, setDisableButtons] = useState(false);
    const pause = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pause" viewBox="0 0 16 16">
        <path d="M6 3.5a.5.5 0 0 1 .5.5v8a.5.5 0 0 1-1 0V4a.5.5 0 0 1 .5-.5zm4 0a.5.5 0 0 1 .5.5v8a.5.5 0 0 1-1 0V4a.5.5 0 0 1 .5-.5z" />
    </svg>;
    const play = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-play" viewBox="0 0 16 16">
        <path d="M10.804 8 5 4.633v6.734L10.804 8zm.792-.696a.802.802 0 0 1 0 1.392l-6.363 3.692C4.713 12.69 4 12.345 4 11.692V4.308c0-.653.713-.998 1.233-.696l6.363 3.692z" />
    </svg>;
    const reset = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-shuffle" viewBox="0 0 16 16">
        <path fill-rule="evenodd" d="M0 3.5A.5.5 0 0 1 .5 3H1c2.202 0 3.827 1.24 4.874 2.418.49.552.865 1.102 1.126 1.532.26-.43.636-.98 1.126-1.532C9.173 4.24 10.798 3 13 3v1c-1.798 0-3.173 1.01-4.126 2.082A9.624 9.624 0 0 0 7.556 8a9.624 9.624 0 0 0 1.317 1.918C9.828 10.99 11.204 12 13 12v1c-2.202 0-3.827-1.24-4.874-2.418A10.595 10.595 0 0 1 7 9.05c-.26.43-.636.98-1.126 1.532C4.827 11.76 3.202 13 1 13H.5a.5.5 0 0 1 0-1H1c1.798 0 3.173-1.01 4.126-2.082A9.624 9.624 0 0 0 6.444 8a9.624 9.624 0 0 0-1.317-1.918C4.172 5.01 2.796 4 1 4H.5a.5.5 0 0 1-.5-.5z" />
        <path d="M13 5.466V1.534a.25.25 0 0 1 .41-.192l2.36 1.966c.12.1.12.284 0 .384l-2.36 1.966a.25.25 0 0 1-.41-.192zm0 9v-3.932a.25.25 0 0 1 .41-.192l2.36 1.966c.12.1.12.284 0 .384l-2.36 1.966a.25.25 0 0 1-.41-.192z" />
    </svg>;
    const unfill = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-square" viewBox="0 0 16 16">
        <path d="M14 1a1 1 0 0 1 1 1v12a1 1 0 0 1-1 1H2a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1h12zM2 0a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z" />
    </svg>;
    const fill = <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-square-fill" viewBox="0 0 16 16">
        <path d="M0 2a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V2z" />
    </svg>;
    const [stopButtonText, setStopButtonText] = useState(pause);
    const [rectText, setRectText] = useState(unfill);
    const [timetaken, setTimeTaken] = useState(0);
    const handleClick = async (e, func) => {
        if (disableButtons) {
            return;
        }
        setDisableButtons(true);
        let t1 = performance.now();
        let t2;
        await func(props.array, props.colors, props.size, props.ms)

        t2 = performance.now();
        setTimeTaken(t2 - t1);
        isArrayReset = false;
        setDisableButtons(false);
    }

    return (
        <div className="menu">
            <div className="controls">
                <div className="sliders">
                    <label>values: </label>
                    <input
                        disabled={disableButtons}
                        type='range'
                        min={0}
                        max={17}
                        value={props.stepIndex}
                        onChange={props.handleChange}
                    />
                    <br />
                    <label>ms: </label>
                    <input
                        disabled={disableButtons}
                        type='range'
                        step={1}
                        min={0}
                        max={1000}
                        value={props.ms}
                        onChange={props.handleDelayChange}
                    />
                </div>
                <button onClick={() => {
                    isSortingPaused = !isSortingPaused
                    isSortingPaused === false ? setStopButtonText(pause) : setStopButtonText(play);
                }}>{stopButtonText}</button>
                <button onClick={async (e) => {
                    /*setStopButtonText(pause);
                    setTimeTaken(0);*/
                    //isArrayReset = true;
                    //isSortingPaused = false;
                    await init(
                        props.array,
                        props.colors,
                        props.size,
                        props.height
                    ).then(isArrayReset = true)
                }}>{reset}</button>
                <button onClick={(e) => {
                    if (props.stroke)
                        setRectText(unfill)
                    else
                        setRectText(fill)
                }
                }>{rectText}</button>
                <p>{timetaken} ms</p>
            </div>
            <div>
                <div className="sorts">
                    {sorts.map((sort, index) => {
                        return <button key={index} disabled={disableButtons} onClick={
                            async (e) => handleClick(e, sort)
                        }>{sort.name}</button>;
                    })
                    }
                    <button onClick={(e) => handleClick(e, bubble)}>b</button>
                </div>
                <div className="more">
                    <h1>SAV</h1>
                    <h3> - Sorting Algorithm Visualizer</h3>
                    <a href="/">
                        <img
                            src={'http://pngimg.com/uploads/github/github_PNG40.png'}
                            height="50px" /></a>
                </div>
            </div>
        </div >
    )
}


export default Menu;
